mapper.select:
select
STR_TO_DATE(spro.sales_reg_date,'%Y%m%d') as sales_reg_date , spro.manufacturer , spro.large_classifi , spro.small_classifi , spro.product_cd , spro.goal_amt , spro.memo , ifnull((SELECT(ROUND((rc.estimate_cnt/spro.goal_amt) * 100))),0) as a_rate , ifnull(rc.estimate_cnt,0) as estimate_cnt , spro.loginID , sr.prod_nm , sr.m_ct_nm , sr.l_ct_nm
from tb_sales_pro spro left outer join ( select ri.loginID -- 수주정보 / 수주제품정보 테이블 조인 , ri.receive_date , rp.receive_num , sum(rp.estimate_cnt) as estimate_cnt , rp.product_cd from receive_prod rp inner join tb_receive_info ri on rp.receive_num = ri.receive_num group by ri.loginID, ri.receive_date, rp.product_cd ) rc on spro.loginID = rc.loginID and DATE_FORMAT(rc.receive_date,'%Y%m%d') = spro.sales_reg_date and rc.product_cd = spro.product_cd left outer join ( select sp.product_cd , sp.prod_nm , scmC.m_ct_nm , scmC.m_ct_cd , scmC.l_ct_nm , scmC.l_ct_cd from (select scmMc.m_ct_nm , scmMc.m_ct_cd , scmLc.l_ct_nm , scmLc.l_ct_cd from tb_scm_l_category scmLc inner join tb_scm_m_category scmMc -- 대분류/ 중분류 정보 테이블 조인 on scmLc.l_ct_cd = scmMc.l_ct_cd ) scmC inner join tb_scm_product sp on sp.m_ct_cd = scmC.m_ct_cd -- 제품정보 테이블과 조인 ) sr on spro.large_classifi = sr.l_ct_cd and spro.small_classifi = sr.m_ct_cd and spro.product_cd = sr.product_cd
and spro.loginID = #{loginID}
and spro.loginID = #{loginID_header}
and spro.large_classifi = #{large_classifi}
and spro.small_classifi = #{small_classifi}
and spro.product_cd = #{product_cd}
and CONCAT(substring(spro.sales_reg_date,1,4),"-",substring(spro.sales_reg_date,5,2),"-",substring(spro.sales_reg_date,7,2)) = #{sales_reg_date}
order by sales_reg_date desc, spro.goal_amt desc, a_rate desc LIMIT #{pageIndex}, #{pageSize}

SELECT COUNT(1) AS tot_cnt FROM tb_sales_pro



	       		and loginID = #{loginID}



	       			and loginID = #{loginID_header}	




	       and large_classifi = #{large_classifi}


	       and small_classifi = #{small_classifi}	


	       and product_cd = #{product_cd}


	       and CONCAT(substring(sales_reg_date,1,4),"-",substring(sales_reg_date,5,2),"-",substring(sales_reg_date,7,2)) = #{sales_reg_date}
